 */
contract DepositAAVE {
    function deposit(
        address _asset,
        uint256 _amount,
        address _ben,
        address _LPAP
    ) public payable {
        ILendingPoolAddressesProvider lpap = ILendingPoolAddressesProvider(_LPAP);
        ILendingPool pool = ILendingPool(lpap.getLendingPool());
