                vault,
                IVault.SwapKind.GIVEN_IN,
                address(bal),
                address(weth),
                balWethPoolId,
                balBalance,
                0,
                address(this),
                address(this)
            );
        }

        uint256 wethBalance = weth.balanceOf(address(this));
        uint256 amountOut = 0;
        if (wethBalance > 1e9) {
            amountOut = UniswapV3Library.multiSwap(
                uniswapV3Router,
                address(weth),
                address(usdc),
                address(dai),
                500,
                100,
                address(this),
                wethBalance,
                0
            );
        }

        return amountOut;
    }
}
