","keccak256":"0xa2f576be637946f767aa56601c26d717f48a0aff44f82e46f13807eea1009a21","license":"MIT"},"contracts/StrategyTarotSupplyVaultUsdc.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./core/Strategy.sol";
import "./connectors/tarot/interfaces/ISupplyVault.sol";
import "./connectors/tarot/interfaces/IBorrowable.sol";
import "./connectors/tarot/interfaces/ISupplyVaultRouter01.sol";

contract StrategyTarotSupplyVaultUsdc is Strategy {

    IERC20 public usdcToken;
    ISupplyVault public tUsdcToken;
    IBorrowable public bTarotSpiritToken;

    uint256 public bTarotSpiritTokenDenominator;

    ISupplyVaultRouter01 public tarotSupplyVaultRouter;


    // --- events

    event StrategyTarotSupplyVaultUsdcUpdatedTokens(address usdcToken, address tUsdcToken, address bTarotSpiritToken,
        uint256 bTarotSpiritTokenDenominator);

    event StrategyTarotSupplyVaultUsdcUpdatedParams(address tarotSupplyVaultRouter);


    // ---  constructor

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    function initialize() initializer public {
        __Strategy_init();
    }


    // --- Setters

    function setTokens(
        address _usdcToken,
        address _tUsdcToken,
        address _bTarotSpiritToken
    ) external onlyAdmin {

        require(_usdcToken != address(0), "Zero address not allowed");
        require(_tUsdcToken != address(0), "Zero address not allowed");
        require(_bTarotSpiritToken != address(0), "Zero address not allowed");

        usdcToken = IERC20(_usdcToken);
        tUsdcToken = ISupplyVault(_tUsdcToken);
        bTarotSpiritToken = IBorrowable(_bTarotSpiritToken);

        bTarotSpiritTokenDenominator = 10 ** bTarotSpiritToken.decimals();

        emit StrategyTarotSupplyVaultUsdcUpdatedTokens(_usdcToken, _tUsdcToken, _bTarotSpiritToken, bTarotSpiritTokenDenominator);
    }

    function setParams(
        address _tarotSupplyVaultRouter
    ) external onlyAdmin {

        require(_tarotSupplyVaultRouter != address(0), "Zero address not allowed");

        tarotSupplyVaultRouter = ISupplyVaultRouter01(_tarotSupplyVaultRouter);

        emit StrategyTarotSupplyVaultUsdcUpdatedParams(_tarotSupplyVaultRouter);
    }


    // --- logic

    function _stake(
        address _asset,
        uint256 _amount
    ) internal override {

        require(_asset == address(usdcToken), "Some token not compatible");

        usdcToken.approve(address(tarotSupplyVaultRouter), _amount);

        tarotSupplyVaultRouter.enter(tUsdcToken, _amount, bTarotSpiritToken);
    }

    function _unstake(
        address _asset,
        uint256 _amount,
        address _beneficiary
    ) internal override returns (uint256) {

        require(_asset == address(usdcToken), "Some token not compatible");

        uint256 tUsdcTokenAmount = tUsdcToken.underlyingValuedAsShare(_amount) * 1001 / 1000;

        IERC20(address(tUsdcToken)).approve(address(tarotSupplyVaultRouter), tUsdcTokenAmount);

        uint256 withdrawAmount = tarotSupplyVaultRouter.leave(tUsdcToken, tUsdcTokenAmount);

        return withdrawAmount;
    }

    function _unstakeFull(
        address _asset,
        address _beneficiary
    ) internal override returns (uint256) {

        require(_asset == address(usdcToken), "Some token not compatible");

        uint256 tUsdcTokenAmount = IERC20(address(tUsdcToken)).balanceOf(address(this));

        IERC20(address(tUsdcToken)).approve(address(tarotSupplyVaultRouter), tUsdcTokenAmount);

        uint256 withdrawAmount = tarotSupplyVaultRouter.leave(tUsdcToken, tUsdcTokenAmount);

        return withdrawAmount;
    }

    function netAssetValue() external view override returns (uint256) {
        return _totalValue();
    }

    function liquidationValue() external view override returns (uint256) {
        return _totalValue();
    }

    function _totalValue() internal view returns (uint256) {

        uint256 totalUnderlyingAmount;
        for (uint8 i = 0; i < tUsdcToken.getBorrowablesLength(); i++) {
            IBorrowable borrowable = tUsdcToken.borrowables(i);
            uint256 borrowableAmount = borrowable.balanceOf(address(tUsdcToken));
            uint256 underlyingAmount = borrowableAmount * borrowable.exchangeRateLast() / 10 ** borrowable.decimals();
            totalUnderlyingAmount += underlyingAmount;
        }
        uint256 sharesAmount = IERC20(address(tUsdcToken)).balanceOf(address(this));
        uint256 totalSharesAmount = IERC20(address(tUsdcToken)).totalSupply();
        uint256 underlyingAmount = sharesAmount * totalUnderlyingAmount / totalSharesAmount;

        return underlyingAmount;
    }

    function _claimRewards(address _beneficiary) internal override returns (uint256) {
        return 0;
    }

}
","keccak256":"0x0c94a1958890ad5b8c88207a17625ce3f43fe9cfaa9aae41165f65399c356355","license":"MIT"},"contracts/connectors/tarot/interfaces/IBorrowable.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

interface IBorrowable {
    /*** Tarot ERC20 ***/

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender)
    external
    view
    returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    /*** Pool Token ***/

    event Mint(
        address indexed sender,
        address indexed minter,
        uint256 mintAmount,
        uint256 mintTokens
    );
    event Redeem(
        address indexed sender,
        address indexed redeemer,
        uint256 redeemAmount,
        uint256 redeemTokens
    );
    event Sync(uint256 totalBalance);

    function underlying() external view returns (address);

    function factory() external view returns (address);

    function totalBalance() external view returns (uint256);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function exchangeRate() external returns (uint256);

    function mint(address minter) external returns (uint256 mintTokens);

    function redeem(address redeemer) external returns (uint256 redeemAmount);

    function skim(address to) external;

    function sync() external;

    function _setFactory() external;

    /*** Borrowable ***/

    event BorrowApproval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Borrow(
        address indexed sender,
        address indexed borrower,
        address indexed receiver,
        uint256 borrowAmount,
        uint256 repayAmount,
        uint256 accountBorrowsPrior,
        uint256 accountBorrows,
        uint256 totalBorrows
    );
    event Liquidate(
        address indexed sender,
        address indexed borrower,
        address indexed liquidator,
        uint256 seizeTokens,
        uint256 repayAmount,
        uint256 accountBorrowsPrior,
        uint256 accountBorrows,
        uint256 totalBorrows
    );

    function BORROW_FEE() external pure returns (uint256);

    function collateral() external view returns (address);

    function reserveFactor() external view returns (uint256);

    function exchangeRateLast() external view returns (uint256);

    function borrowIndex() external view returns (uint256);

    function totalBorrows() external view returns (uint256);

    function borrowAllowance(address owner, address spender)
    external
    view
    returns (uint256);

    function borrowBalance(address borrower) external view returns (uint256);

    function borrowTracker() external view returns (address);

    function BORROW_PERMIT_TYPEHASH() external pure returns (bytes32);

    function borrowApprove(address spender, uint256 value)
    external
    returns (bool);

    function borrowPermit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    function borrow(
        address borrower,
        address receiver,
        uint256 borrowAmount,
        bytes calldata data
    ) external;

    function liquidate(address borrower, address liquidator)
    external
    returns (uint256 seizeTokens);

    function trackBorrow(address borrower) external;

    /*** Borrowable Interest Rate Model ***/

    event AccrueInterest(
        uint256 interestAccumulated,
        uint256 borrowIndex,
        uint256 totalBorrows
    );
    event CalculateKink(uint256 kinkRate);
    event CalculateBorrowRate(uint256 borrowRate);

    function KINK_BORROW_RATE_MAX() external pure returns (uint256);

    function KINK_BORROW_RATE_MIN() external pure returns (uint256);

    function KINK_MULTIPLIER() external pure returns (uint256);

    function borrowRate() external view returns (uint256);

    function kinkBorrowRate() external view returns (uint256);

    function kinkUtilizationRate() external view returns (uint256);

    function adjustSpeed() external view returns (uint256);

    function rateUpdateTimestamp() external view returns (uint32);

    function accrualTimestamp() external view returns (uint32);

    function accrueInterest() external;

    /*** Borrowable Setter ***/

